        public danisman CurrentUser
        {
            get
            {
                if (Session["User"] != null)
                {
                    DateTime d = DateTime.Now;
                    bool h = db.histories.Any(x => x.DanismanId == CurrentUser.Id && x.CreatedDate.Value.Year == d.Year && x.CreatedDate.Value.Month == d.Month && x.CreatedDate.Value.Day == d.Day && x.Action.Contains("sisteme giriş yapmıştır"));
                    if (!h)
                    {
                        LogEkle($"{CurrentUser.Ad}, sisteme giriş yapmıştır.", true);
                    }
                    return Session["User"] as danisman;


                }

                return null;
            }
        }

        public void LogEkle(string action, bool admin)
        {
            history h = new history();
            h.DanismanId = CurrentUser.Id;
            h.CreatedDate = DateTime.Now;
            h.Action = action;
            h.forAdmin = admin;
            db.histories.Add(h);
            db.SaveChanges();
        }

        public string ResimEkleWithMark(HttpPostedFileBase item)
        {
            string yol = "/Images/" + Guid.NewGuid() + Path.GetExtension(item.FileName);
            var siteUrl = Request.Url.GetLeftPart(UriPartial.Authority);
            WebClient wc = new WebClient();
            byte[] bytes = wc.DownloadData(siteUrl+"/Theme/images/back.png");
            MemoryStream ms = new MemoryStream(bytes);
            Image marker2 = Image.FromStream(ms);
            Image frame = Image.FromStream(item.InputStream);

            int width = frame.Width;
            int height = frame.Height;

            int size = width < height ? width/2 : height/2;
            Bitmap marker = new Bitmap(marker2, new Size(size, size/2));
            using (frame)
            {
                
                using (var bitmap = new Bitmap(width, height))
                {
                    using (var canvas = Graphics.FromImage(bitmap))
                    {
                        canvas.InterpolationMode = InterpolationMode.HighQualityBicubic;
                        canvas.DrawImage(frame,
                                         new Rectangle(0,
                                                       0,
                                                       width,
                                                       height),
                                         new Rectangle(0,
                                                       0,
                                                       frame.Width,
                                                       frame.Height),
                                         GraphicsUnit.Pixel);
                        canvas.DrawImage(marker,
                                         (bitmap.Width / 2) - (marker.Width / 2),
                                         (bitmap.Height / 2) - (marker.Height / 2));
                        canvas.Save();
                    }

                    bitmap.Save(Server.MapPath(yol), ImageFormat.Jpeg);
                }
            }

            return yol;
        }

        private ImageCodecInfo GetEncoder(ImageFormat format)
        {
            ImageCodecInfo[] codecs = ImageCodecInfo.GetImageDecoders();
            foreach (ImageCodecInfo codec in codecs)
            {
                if (codec.FormatID == format.Guid)
                {
                    return codec;
                }
            }
            return null;
        }

        public string ResimBelgeEkle(HttpPostedFileBase Resim)
        {
            string yol = "/Images/" + Guid.NewGuid() + Path.GetExtension(Resim.FileName);
            string uzanti = Path.GetExtension(Resim.FileName).ToLower();
            if (uzanti == ".jpg" || uzanti == ".jpeg" || uzanti == ".png" || uzanti == ".gif")
            {
                using (Image img = Image.FromStream(Resim.InputStream))
                {
                    ImageCodecInfo jpgEncoder = GetEncoder(ImageFormat.Jpeg);
                    Encoder myEncoder = Encoder.Quality;
                    EncoderParameters myEncoderParameters = new EncoderParameters(1);
                    EncoderParameter myEncoderParameter = new EncoderParameter(myEncoder, 90L);
                    myEncoderParameters.Param[0] = myEncoderParameter;
                    img.Save(Server.MapPath(yol), jpgEncoder, myEncoderParameters);
                }
            }
            else
            {
                Resim.SaveAs(Server.MapPath(yol));
            }

            return yol;
        }

